CREATE TABLE IF NOT EXISTS `ims_ewei_shop_sale_coupon_data` ( `id` int(11) NOT NULL AUTO_INCREMENT, `uniacid` int(11) DEFAULT '0', `openid` varchar(255) DEFAULT '', `couponid` int(11) DEFAULT '0', `gettime` int(11) DEFAULT '0', `gettype` tinyint(3) DEFAULT '0', `usedtime` int(11) DEFAULT '0', `orderid` int(11) DEFAULT '0', PRIMARY KEY (`id`), KEY `idx_uniacid` (`uniacid`), KEY `idx_couponid` (`couponid`), KEY `idx_gettime` (`gettime`), KEY `idx_gettype` (`gettype`), KEY `idx_usedtime` (`usedtime`), KEY `idx_orderid` (`orderid`) ) ENGINE=MyISAM DEFAULT CHARSET=utf8 AUTO_INCREMENT=1 ;
CREATE TABLE IF NOT EXISTS `ims_ewei_shop_store` ( `id` int(11) NOT NULL AUTO_INCREMENT, `uniacid` int(11) DEFAULT '0', `storename` varchar(255) DEFAULT '', `address` varchar(255) DEFAULT '', `tel` varchar(255) DEFAULT '', `lat` varchar(255) DEFAULT '', `lng` varchar(255) DEFAULT '', `status` tinyint(3) DEFAULT '0', `realname` varchar(255) DEFAULT '', `mobile` varchar(255) DEFAULT '', `fetchtime` varchar(255) DEFAULT '', `type` tinyint(1) DEFAULT '0', `logo` varchar(255) DEFAULT '', `saletime` varchar(255) DEFAULT '', `desc` text, `displayorder` int(11) DEFAULT '0', PRIMARY KEY (`id`), KEY `idx_uniacid` (`uniacid`), KEY `idx_status` (`status`) ) ENGINE=MyISAM DEFAULT CHARSET=utf8 AUTO_INCREMENT=3 ;
CREATE TABLE IF NOT EXISTS `ims_ewei_shop_sysset` ( `id` int(11) NOT NULL AUTO_INCREMENT, `uniacid` int(11) DEFAULT '0', `sets` text, `plugins` text, `sec` text, PRIMARY KEY (`id`), KEY `idx_uniacid` (`uniacid`) ) ENGINE=MyISAM DEFAULT CHARSET=utf8 AUTO_INCREMENT=2 ;
CREATE TABLE IF NOT EXISTS `ims_ewei_shop_system_adv` ( `id` int(11) NOT NULL AUTO_INCREMENT, `title` varchar(255) DEFAULT '', `thumb` varchar(255) DEFAULT '', `url` varchar(255) DEFAULT '', `createtime` int(11) DEFAULT '0', `displayorder` int(11) DEFAULT '0', `module` varchar(255) DEFAULT '', `status` tinyint(3) DEFAULT '0', PRIMARY KEY (`id`) ) ENGINE=MyISAM DEFAULT CHARSET=utf8 AUTO_INCREMENT=1 ;
CREATE TABLE IF NOT EXISTS `ims_ewei_shop_system_article` ( `id` int(11) NOT NULL AUTO_INCREMENT, `title` varchar(255) DEFAULT '', `author` varchar(255) DEFAULT '', `thumb` varchar(255) DEFAULT '', `content` text, `createtime` int(11) DEFAULT '0', `displayorder` int(11) DEFAULT '0', `cate` int(11) DEFAULT '0', `status` tinyint(3) DEFAULT '0', PRIMARY KEY (`id`) ) ENGINE=MyISAM DEFAULT CHARSET=utf8 AUTO_INCREMENT=1 ;
CREATE TABLE IF NOT EXISTS `ims_ewei_shop_system_banner` ( `id` int(11) NOT NULL AUTO_INCREMENT, `title` varchar(255) DEFAULT '', `thumb` varchar(255) DEFAULT '', `url` varchar(255) DEFAULT '', `createtime` int(11) DEFAULT '0', `displayorder` int(11) DEFAULT '0', `status` tinyint(3) DEFAULT '0', PRIMARY KEY (`id`) ) ENGINE=MyISAM DEFAULT CHARSET=utf8 AUTO_INCREMENT=1 ;
CREATE TABLE IF NOT EXISTS `ims_ewei_shop_system_case` ( `id` int(11) NOT NULL AUTO_INCREMENT, `title` varchar(255) DEFAULT '', `thumb` varchar(255) DEFAULT '', `qr` varchar(255) DEFAULT '', `displayorder` int(11) DEFAULT '0', `status` tinyint(3) DEFAULT '0', PRIMARY KEY (`id`) ) ENGINE=MyISAM DEFAULT CHARSET=utf8 AUTO_INCREMENT=1 ;
CREATE TABLE IF NOT EXISTS `ims_ewei_shop_system_category` ( `id` int(11) NOT NULL AUTO_INCREMENT, `name` varchar(255) DEFAULT '', `displayorder` int(11) DEFAULT '0', `status` int(11) DEFAULT '0', PRIMARY KEY (`id`) ) ENGINE=MyISAM DEFAULT CHARSET=utf8 AUTO_INCREMENT=1 ;
