CREATE TABLE IF NOT EXISTS `ims_ewei_shop_system_guestbook` ( `id` int(11) NOT NULL AUTO_INCREMENT, `title` varchar(255) NOT NULL DEFAULT '', `content` varchar(255) NOT NULL DEFAULT '', `nickname` varchar(255) NOT NULL DEFAULT '', `createtime` int(11) DEFAULT NULL, `email` varchar(255) NOT NULL DEFAULT '', `clientip` varchar(64) NOT NULL DEFAULT '', `mobile` varchar(11) NOT NULL DEFAULT '', PRIMARY KEY (`id`) ) ENGINE=MyISAM DEFAULT CHARSET=utf8 AUTO_INCREMENT=1 ;
CREATE TABLE IF NOT EXISTS `ims_ewei_shop_system_link` ( `id` int(11) NOT NULL AUTO_INCREMENT, `name` varchar(255) NOT NULL DEFAULT '', `url` varchar(255) NOT NULL DEFAULT '', `thumb` varchar(255) NOT NULL DEFAULT '', `displayorder` int(11) DEFAULT NULL, `status` tinyint(3) DEFAULT NULL, PRIMARY KEY (`id`) ) ENGINE=MyISAM DEFAULT CHARSET=utf8 AUTO_INCREMENT=1 ;
CREATE TABLE IF NOT EXISTS `ims_ewei_shop_system_site` ( `id` int(11) unsigned NOT NULL AUTO_INCREMENT, `type` varchar(32) NOT NULL DEFAULT '', `content` text, PRIMARY KEY (`id`) ) ENGINE=MyISAM DEFAULT CHARSET=utf8 AUTO_INCREMENT=1 ;
CREATE TABLE IF NOT EXISTS `ims_ewei_shop_virtual_category` ( `id` int(11) NOT NULL AUTO_INCREMENT, `uniacid` int(11) DEFAULT '0', `name` varchar(50) DEFAULT NULL, PRIMARY KEY (`id`), KEY `idx_uniacid` (`uniacid`) ) ENGINE=MyISAM DEFAULT CHARSET=utf8 AUTO_INCREMENT=2 ;
CREATE TABLE IF NOT EXISTS `ims_ewei_shop_virtual_data` ( `id` int(11) NOT NULL AUTO_INCREMENT, `uniacid` int(11) NOT NULL DEFAULT '0', `typeid` int(11) NOT NULL DEFAULT '0', `pvalue` varchar(255) DEFAULT '', `fields` text NOT NULL, `openid` varchar(255) NOT NULL DEFAULT '', `usetime` int(11) NOT NULL DEFAULT '0', `orderid` int(11) DEFAULT '0', `ordersn` varchar(255) DEFAULT '', `price` decimal(10,2) DEFAULT '0.00', PRIMARY KEY (`id`), KEY `idx_uniacid` (`uniacid`), KEY `idx_typeid` (`typeid`), KEY `idx_usetime` (`usetime`), KEY `idx_orderid` (`orderid`) ) ENGINE=MyISAM DEFAULT CHARSET=utf8 AUTO_INCREMENT=1001 ;
CREATE TABLE IF NOT EXISTS `ims_ewei_shop_virtual_type` ( `id` int(11) NOT NULL AUTO_INCREMENT, `uniacid` int(11) NOT NULL DEFAULT '0', `cate` int(11) DEFAULT '0', `title` varchar(255) NOT NULL DEFAULT '', `fields` text NOT NULL, `usedata` int(11) NOT NULL DEFAULT '0', `alldata` int(11) NOT NULL DEFAULT '0', PRIMARY KEY (`id`), KEY `idx_uniacid` (`uniacid`), KEY `idx_cate` (`cate`) ) ENGINE=MyISAM DEFAULT CHARSET=utf8 AUTO_INCREMENT=2 ;
