CREATE TABLE IF NOT EXISTS `ims_wmb_goshare_convert` ( `id` int(11) NOT NULL AUTO_INCREMENT, `weid` int(11) NOT NULL, `themeid` int(11) NOT NULL, `themename` varchar(200) NOT NULL, `openid` varchar(200) NOT NULL, `giftid` int(11) NOT NULL, `giftname` varchar(100) NOT NULL, `code` varchar(100) NOT NULL, `codetime` varchar(100) NOT NULL, `istake` int(2) NOT NULL, `cookieid` varchar(200) NOT NULL, PRIMARY KEY (`id`) ) ENGINE=MyISAM DEFAULT CHARSET=utf8 AUTO_INCREMENT=1 ;
CREATE TABLE IF NOT EXISTS `ims_wmb_goshare_cookie` ( `id` int(11) NOT NULL AUTO_INCREMENT, `weid` int(11) NOT NULL, `themeid` int(11) NOT NULL, `openid` varchar(200) NOT NULL, `cookieid` varchar(200) NOT NULL, PRIMARY KEY (`id`) ) ENGINE=MyISAM DEFAULT CHARSET=utf8 AUTO_INCREMENT=1 ;
CREATE TABLE IF NOT EXISTS `ims_wmb_goshare_gift` ( `id` int(11) NOT NULL AUTO_INCREMENT, `weid` int(11) NOT NULL, `themeid` int(11) NOT NULL, `groupid` int(11) NOT NULL, `groupname` varchar(200) NOT NULL, `stdname` varchar(100) NOT NULL, `unit` varchar(10) NOT NULL, `desc` varchar(100) NOT NULL, `amount` int(11) NOT NULL, `needscore` int(11) NOT NULL, `sort` int(11) NOT NULL, `left` int(11) NOT NULL, PRIMARY KEY (`id`) ) ENGINE=MyISAM DEFAULT CHARSET=utf8 AUTO_INCREMENT=1 ;
CREATE TABLE IF NOT EXISTS `ims_wmb_goshare_giftgroup` ( `id` int(11) NOT NULL AUTO_INCREMENT, `weid` int(11) NOT NULL, `themeid` int(11) NOT NULL, `themename` varchar(200) NOT NULL, `groupname` varchar(100) NOT NULL, `groupstate` int(11) NOT NULL, PRIMARY KEY (`id`) ) ENGINE=MyISAM DEFAULT CHARSET=utf8 AUTO_INCREMENT=1 ;
CREATE TABLE IF NOT EXISTS `ims_wmb_goshare_member` ( `id` int(11) NOT NULL AUTO_INCREMENT, `weid` int(11) NOT NULL, `openid` varchar(200) NOT NULL, `themeid` int(11) NOT NULL, `score` int(11) NOT NULL, `cookieid` varchar(200) NOT NULL, `helpid` varchar(200) NOT NULL, PRIMARY KEY (`id`) ) ENGINE=MyISAM DEFAULT CHARSET=utf8 AUTO_INCREMENT=1 ;
CREATE TABLE IF NOT EXISTS `ims_wmb_goshare_theme` ( `id` int(11) NOT NULL AUTO_INCREMENT, `weid` int(11) NOT NULL, `groupid` int(11) NOT NULL, `groupname` varchar(200) NOT NULL, `themename` varchar(100) NOT NULL, `headtitle` varchar(200) NOT NULL, `themetitle` varchar(1000) NOT NULL, `themelogo` varchar(512) NOT NULL, `undertaker` varchar(200) NOT NULL, `ad1` varchar(200) NOT NULL, `ad1content` varchar(1000) NOT NULL, `ad2` varchar(200) NOT NULL, `ad2content` varchar(1000) NOT NULL, `ad3` varchar(200) NOT NULL, `ad3content` varchar(1000) NOT NULL, `ad3pic` varchar(512) NOT NULL, `url` varchar(1000) NOT NULL, `place` varchar(200) NOT NULL, `tel` varchar(30) NOT NULL, `begintime` date NOT NULL, `endtime` date NOT NULL, `footpic` varchar(512) NOT NULL, `overtitle` varchar(1000) NOT NULL, `sharepic` varchar(512) NOT NULL, PRIMARY KEY (`id`) ) ENGINE=MyISAM DEFAULT CHARSET=utf8 AUTO_INCREMENT=1 ;
CREATE TABLE IF NOT EXISTS `ims_wmb_goshare_transmit` ( `id` int(11) NOT NULL AUTO_INCREMENT, `weid` int(11) NOT NULL, `themeid` int(11) NOT NULL, `openid` varchar(200) NOT NULL, `helpid` varchar(200) NOT NULL, `cookieid` varchar(200) NOT NULL, `helpcookid` varchar(200) NOT NULL, PRIMARY KEY (`id`) ) ENGINE=MyISAM DEFAULT CHARSET=utf8 AUTO_INCREMENT=1 ;
